
public class swapping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  int x = 22, y = 53;
			 
	        System.out.println("Before Swap");
	        System.out.println("x = " + x);
	        System.out.println("y = " + y);
	 
	        int temp = x;
	        x = y;
	        y = temp;
	 
	        System.out.println("After swap");
	        System.out.println("x = " + x);
	        System.out.println("y = " + y);
	    }
	}


